import React, { useState } from 'react';
import { makeStyles } from '@material-ui/core/styles';
import Grid from '@material-ui/core/Grid';
import CardMedia from '@material-ui/core/CardMedia';
import HomeUsersList from './UserList';
import NewUserButton from "../../components/NewUserButton/NewUserButton.jsx";
import SearchUserBar from "../../components/SearchUserBar/SearchUserBar.jsx"

const useStyles = makeStyles((theme) => ({
  root: {
    flexGrow: 1,
  },
  paper: {
    padding: theme.spacing(2),
    textAlign: 'center',
    color: theme.palette.text.secondary,
  },
  usersWrapper: {

    width: '100%',
    maxWidth: '1400px',
    margin: '0 auto',
    paddingBottom: '250px',
  },
  usersHeader: {
    maxWidth: '1350px',
    justifyContent: "space-between",
    display: "flex",
    margin: "0 auto",
    marginTop: '70px',
  },
  h1: {
    fontSize: "40px",
  },
}));

function Home() {
  const classes = useStyles();
  const [users, setUsers] = useState([1, 2, 3, 4, 5, 6, 7, 8]);

  function AddUser(num) {
    setUsers((prevState) => [...prevState, num  ])
  }

  return (
    <>
      <div className={classes.usersHeader}>
        <h1 className={classes.h1}>Users</h1>
        {/* <SearchUserBar /> */}
        <NewUserButton AddUser={AddUser}/>
      </div>
      <Grid
        className={classes.usersWrapper}
        container
        spacing={5}
        justify="center"
      >
        <HomeUsersList users={users} />
      </Grid>
    </>
  );
}

export default Home;
